#!/usr/bin/python
#coding:utf-8

import glob
import random

n = glob.bch_n
k = glob.bch_k
'''------------------产生0-1数---------------------'''
def rand():
    if random.random() > 0.5:
        ran = 1
    else :
        ran = 0
    return ran
'''------------------产生k位0-1数列表---------------'''
def rand_data():
    random_data = []
    for i in range (k):
        random_data.append(rand())
    return random_data

def static_data():
    static_data = glob.gf2mod()
    static_len = len(static_data)
    if  static_len < k:
        for i in range(k - static_len):
            static_data.insert(0,0)
    return static_data


